import React, { useState } from "react";

function App() {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  async function generate() {
    setLoading(true);
    setResponse(null);

    // const res = await fetch("http://localhost:8000/generate", {
    //   method: "POST",
    //   headers: { "Content-Type": "application/json" },
    //   body: JSON.stringify({ text: query }),
    // });

    const API_URL = "https://ondemand.orc.gmu.edu/rnode/gpu020.orc.gmu.edu/36833/proxy/8000";



const res = await fetch(`${API_URL}/generate`, {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({ text: query }),
});


    const data = await res.json();
    setResponse(data);
    setLoading(false);
  }

  return (
    <div style={{ padding: 30, maxWidth: 700, margin: "auto" }}>
      <h1>AI Travel Planner</h1>

      <textarea
        rows="4"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Enter your travel request..."
        style={{ width: "100%", padding: 12 }}
      />

      <button onClick={generate} disabled={loading} style={{ marginTop: 10 }}>
        {loading ? "Generating..." : "Generate Itinerary"}
      </button>

      {response && (
        <div style={{ marginTop: 30, padding: 20, border: "1px solid gray" }}>
          <h3>Extracted Preference:</h3>
          <p>{response.preference}</p>

          <h3>Generated Itinerary:</h3>
          <pre>{response.itinerary}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
